function woohoo_post_like()
{
    jQuery(document).on('click', '.post-like a' , function () {
        heart   = jQuery(this);
        post_id = heart.data( "post_id" );
        jQuery.ajax({
            type    : "post",
            url     : aia.config.ajaxurl,
            data    : "action=woohoo_post_like&nonce="+aia.config.nonce+"&bdaia_post_like=&post_id="+post_id,
            success: function(e) {
                if (e != "already") {
                    heart.addClass("voted");
                    heart.siblings(".count").text(e)
                }
            }
        }); return false
    });
}





